<node CREATED="1545414125685" ID="ID_1216334619" MODIFIED="1547658153960" TEXT="CA (hors taxable) / CA globale pour obtenir le taux"/>
</node>
<node CREATED="1544561640118" ID="ID_83615262" MODIFIED="1547658153960" TEXT="Coefficient d&apos;admission (CAD)">
<node CREATED="1545414168506" ID="ID_1040053070" MODIFIED="1547658153960" TEXT="Qui prend en compte des cas particuliers"/>
<node CREATED="1545414185983" ID="ID_1425899132" MODIFIED="1547658153960" TEXT="Pour les v&#xe9;hicules de tourismes CAD=0"/>
<node CREATED="1545414204240" ID="ID_221660223" MODIFIED="1547658153960" TEXT="Gazole CAD=0.8"/>
<node CREATED="1545414210072" ID="ID_1075395289" MODIFIED="1547658153970" TEXT="Aucune mesure d&apos;exclusion CAD=1"/>
