<node CREATED="1545414230296" ID="ID_1295729006" MODIFIED="1547658153970" TEXT="Coefficient de d&#xe9;duction">
<node CREATED="1545414242782" ID="ID_1858173987" MODIFIED="1547658153970" TEXT="CAS * CTA * CAD"/>
<node CREATED="1545414268969" ID="ID_709777592" MODIFIED="1547658153970" TEXT="Exemple:">
<node CREATED="1545414279690" ID="ID_1521037234" MODIFIED="1547658153970" TEXT="Imprimantes, CAS=1; CTA=0.75;CAD=1 ==&gt; CD = 0.75"/>
<node CREATED="1545414331829" ID="ID_1554459691" MODIFIED="1547658153970" TEXT="TVA factur&#xe9; = 40 &#x20ac;; TVA ded 30 &#x20ac;"/>
</node>
<node CREATED="1545414358341" ID="ID_672807369" MODIFIED="1547658153970" TEXT="Exemple:">
<node CREATED="1545414363253" ID="ID_1996169082" MODIFIED="1547658153970" TEXT="Loyer, CAS=1; CTA=0; CAD=1 ==&gt; CD=0"/>
<node CREATED="1545414394323" ID="ID_15816781" MODIFIED="1547658153970" TEXT="TVA factur&#xe9; = 1 200&#x20ac;; TVA d&#xe9;d = 0 &#x20ac;"/>
</node>
</node>
