<node CREATED="1547657484543" ID="ID_714483184" MODIFIED="1547658153970" TEXT="Le bien constitue pour le cessionnaire une immobilisation"/>
<node CREATED="1547657500596" ID="ID_860841443" MODIFIED="1547658153970" TEXT="Le bien acquis n&apos;est pas exclus du droit de d&#xe9;duction"/>
<node CREATED="1547657526729" ID="ID_1980220536" MODIFIED="1547658153970" TEXT="TVA ded = TVA factur&#xe9;e par le c&#xe9;dant x N x1/D x CD"/>
